package firstpro;
import java.util.*;
public class loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       System.out.println("Enter the number to run the loop");
       Scanner sc=new Scanner(System.in);
       int val=sc.nextInt();
       for(int i=0;i<val;i++)
       {
    	   System.out.println(i);
       }
       
	}

}
